mapper.select:
SELECT a.symptoms_id as symptoms_id, a.cate_id as cate_id, a.title as title , a.recommend_deptid as recommend_deptid , a.recommend_dept as recommend_dept , a.sort_order as sort_order, a.status as status, date_format(a.publish_time,'%Y-%m-%d %H:%i:%S') as publish_time, date_format(a.add_time,'%Y-%m-%d %H:%i:%S') as add_time, a.add_username as add_username, b.cate_name as cate_name, b.cate_type as cate_type FROM yw_symptoms a , yw_body_category b WHERE 1=1 and a.cate_id = b.cate_id
and a.symptoms_id = #{symptoms_id,jdbcType=INTEGER}
and a.cate_id = #{cate_id,jdbcType=VARCHAR}
and INSTR(a.TITLE, #{title}) > 1
and INSTR(a.recommend_dept, #{recommend_dept}) > 0
and a.sort_order = #{sort_order,jdbcType=VARCHAR}
and a.status = #{status,jdbcType=VARCHAR}
and date_format(a.add_time, '%Y-%m-%d') >= #{add_time_start} and date_format(a.add_time, '%Y-%m-%d') <= #{add_time_end}
and a.add_username = #{add_username,jdbcType=VARCHAR}
and a.cate_type = #{cate_type,jdbcType=VARCHAR}
ORDER BY a.sort_order DESC, a.add_time DESC

SELECT a.symptoms_id as symptoms_id, a.cate_id as cate_id, a.title as title , a.recommend_deptid as recommend_deptid , a.recommend_dept as recommend_dept , a.sort_order as sort_order, a.status as status, date_format(a.publish_time,'%Y-%m-%d %H:%i:%S') as publish_time, date_format(a.add_time,'%Y-%m-%d %H:%i:%S') as add_time, a.add_username as add_username, b.cate_name as cate_name, b.cate_type as cate_type FROM yw_symptoms a , yw_body_category b WHERE 1=1 and a.cate_id = b.cate_id
and a.symptoms_id = #{symptoms_id,jdbcType=INTEGER}
and a.cate_id = #{cate_id,jdbcType=VARCHAR}
and INSTR(a.TITLE, #{title}) > 1
and INSTR(a.recommend_dept, #{recommend_dept}) > 0
and a.sort_order = #{sort_order,jdbcType=VARCHAR}
and a.status = #{status,jdbcType=VARCHAR}
and date_format(a.add_time, '%Y-%m-%d') >= #{add_time_start} and date_format(a.add_time, '%Y-%m-%d') <= #{add_time_end}
and a.add_username = #{add_username,jdbcType=VARCHAR}
and a.cate_type = #{cate_type,jdbcType=VARCHAR}
ORDER BY a.sort_order DESC, a.add_time DESC

SELECT
count(*)
FROM yw_symptoms a , yw_body_category b
WHERE 1=1 and a.cate_id = b.cate_id

    and a.symptoms_id = #{symptoms_id,jdbcType=INTEGER}


    and a.cate_id = #{cate_id,jdbcType=VARCHAR}


       and INSTR(a.TITLE, #{title}) > 1       	 


    	and INSTR(a.recommend_dept, #{recommend_dept})  > 0        


    and a.sort_order = #{sort_order,jdbcType=VARCHAR}


    and a.status = #{status,jdbcType=VARCHAR}


     and date_format(a.add_time, '%Y-%m-%d') >= #{add_time_start} and	date_format(a.add_time, '%Y-%m-%d') <= #{add_time_end} 


    and a.add_username = #{add_username,jdbcType=VARCHAR}


    and a.cate_type = #{cate_type,jdbcType=VARCHAR}

mapper.delete:
DELETE  FROM  yw_symptoms
WHERE 1 = 1
	   AND symptoms_id = #{symptoms_id}
